mapper.select:
SELECT  tab_order.*,tab_user.user_nick from (
	select `id`,`user_id`,`order_num`,`order_date_num`,`order_desk_num`,`order_state`,`order_total`,`order_pay`,`order_discount_total`,`order_commodity_total`,`order_pay_type`,`order_meals_num`,`order_remark`,`order_time`,`order_invoice`,`customer_id`,`create_time` from ff_order

			   and id = #{id} 
		       and user_id = #{userId} 
			   and order_num = #{orderNum} 
			   and order_date_num = #{orderDateNum} 
			   and order_desk_num = #{orderDeskNum} 
			   and order_state = #{orderState} 
			   and order_total = #{orderTotal} 
			   and order_pay = #{orderPay} 
			   and order_discount_total = #{orderDiscountTotal} 
			   and order_commodity_total = #{orderCommodityTotal} 
			   and order_pay_type = #{orderPayType} 
			   and order_meals_num = #{orderMealsNum} 
			   and order_remark = #{orderRemark} 
			   and order_invoice = #{orderInvoice} 
			   and order_time = #{orderTime} 
			   and customer_id = #{customerId} 
			   and create_time = #{createTime} 



			order by ${sort} ${order}


			order by id desc



		limit #{offset}, #{limit}

) as tab_order
JOIN ff_user as tab_user
ON tab_order.user_id = tab_user.id

select count(*) from ff_order

	  and id = #{id} 
	  and user_id = #{userId} 
	  and order_num = #{orderNum} 
	  and order_date_num = #{orderDateNum} 
	  and order_desk_num = #{orderDeskNum} 
	  and order_state = #{orderState} 
	  and order_total = #{orderTotal} 
	  and order_pay = #{orderPay} 
	  and order_discount_total = #{orderDiscountTotal} 
	  and order_commodity_total = #{orderCommodityTotal} 
	  and order_pay_type = #{orderPayType} 
	  and order_meals_num = #{orderMealsNum} 
	  and order_remark = #{orderRemark} 
	  and order_invoice = #{orderInvoice} 
	  and order_time = #{orderTime} 
	  and customer_id = #{customerId} 
	  and create_time = #{createTime}

		select A.id, A.`order_num`,A.`order_state`,A.`order_pay`,A.`order_commodity_total` as `commodity_total`,B.`out_title` as `title`,A.`create_time`,C.commodit_img,A.`order_date_num`    from
		(select `id`,`order_num`,`order_state`,`order_pay`,`order_commodity_total`,`create_time`,`order_date_num` from ff_order

			order_state!=101
			 AND user_id = #{userId} 
			 AND customer_id = #{customerId} 

				AND order_state IN

					#{item}



	    order by `create_time` desc

			limit #{offset}, #{limit}

		) as A
		LEFT JOIN ff_order_detial B on A.id  =  B.order_id and B.id=(select min(id) from ff_order_detial where order_id=A.id and out_type=1)
        LEFT JOIN ff_commodit C
        on C.id  = B.out_id

SELECT a.id,a.order_num,a.order_state,a.order_total,a.order_pay,a.order_date_num,a.order_desk_num,a.order_time,b.user_nick,b.user_icon from ff_order  as a
left JOIN ff_user as b
on a.user_id = b.id
where  a.customer_id=#{customerId}
and a.order_time BETWEEN #{startTime} and #{endTime}


		AND a.id > #{lastId}


		AND a.id < #{lastId}


ORDER BY a.id DESC
